# FileName:check_bmp.py
# -*- coding: utf-8 -*-
"""  struct 应用 读取bmp头部信息 """

import struct
import base64

bmp_header = b'\x42\x4d\x38\x8c\x0a\x00\x00\x00\x00\x00\x36\x00\x00\x00\x28\x00\x00\x00\x80\x02\x00\x00\x68\x01\x00\x00\x01\x00\x18\x00'

print(struct.unpack('<ccIIIIIIHH', bmp_header))

# 检查任意文件是否位图
bmp_data = base64.b64decode('')


def bmp_info(data):
    T = struct.unpack('<ccIIIIIIHH', data[:30])
    if T[0] == b'B' and T[1] == b'M':
        return {
            'width': T[6],
            'height': T[7],
            'color': T[9]
        }
    else:
        print('不是位图')


# 测试
bi = bmp_info(bmp_data)
print(bi)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')

